// auth.service.ts - CORRIJA com withCredentials
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { BehaviorSubject } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  private apiUrl = 'http://localhost:3000';
  private authenticatedSubject = new BehaviorSubject<boolean>(false);
  public authenticated$ = this.authenticatedSubject.asObservable();

  constructor(private http: HttpClient, private router: Router) {
    this.checkAuthentication();
  }

  checkAuthentication() {
    this.http.get<{authenticated: boolean}>(
      `${this.apiUrl}/api/user`, 
      { 
        withCredentials: true // ✅ CRÍTICO: permite cookies entre domínios
      }
    ).subscribe({
      next: (response) => {
        console.log('Auth status:', response.authenticated);
        this.authenticatedSubject.next(response.authenticated);

        if (response.authenticated) {
          this.router.navigate(['/dashboard']);
        }
      },
      error: (error) => {
        console.error('Auth check error:', error);
        this.authenticatedSubject.next(false);
      }
    });
  }

  isAuthenticated(): boolean {
    return this.authenticatedSubject.value;
  }

  loginWithGoogle() {
    window.location.href = `${this.apiUrl}/auth/google`;
  }

  logout() {
    this.http.get(
      `${this.apiUrl}/logout`, 
      { withCredentials: true }
    ).subscribe(() => {
      this.authenticatedSubject.next(false);
      this.router.navigate(['/login']);
    });
  }
}
