// src/app/chat-gpt/chat-gpt.service.ts
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ChatGptService {

  private apiUrl = 'http://localhost:3000/api';

  constructor(private http: HttpClient) { }

  getUser(): Observable<any> {
    return this.http.get(`${this.apiUrl}/user`, { withCredentials: true });
  }

  sendPrompt(prompt: string, ai: string): Observable<any> {
    return this.http.post(`${this.apiUrl}/prompt`, { prompt, ai }, { withCredentials: true });
  }
}
