// dashboard.component.ts - COMPLETO
import { Component, OnInit } from '@angular/core';
import { AuthService } from '../auth.service';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  user: any;
  ais: any[] = [];
  prompt: string = '';
  selectedAI: string = 'gpt';
  response: string = '';
  loading: boolean = false;

  constructor(
    private authService: AuthService,
    private router: Router,
    private http: HttpClient
  ) {}

  ngOnInit() {
    if (!this.authService.isAuthenticated()) {
      this.router.navigate(['/login']);
    }

    this.loadUserInfo();
    this.loadAIs();
  }

  loadUserInfo() {
    this.http.get<any>('http://localhost:3000/api/user', 
      { withCredentials: true }
    ).subscribe({
      next: (response) => {
        this.user = response.user;
      },
      error: () => {
        this.authService.logout();
      }
    });
  }

  loadAIs() {
    this.http.get<any>('http://localhost:3000/api/ais',
      { withCredentials: true }
    ).subscribe({
      next: (response) => {
        this.ais = response.ais;
      },
      error: (error) => {
        console.error('Error loading AIs:', error);
      }
    });
  }

  sendPrompt() {
    if (!this.prompt.trim()) return;

    this.loading = true;
    this.response = '';

    this.http.post<any>('http://localhost:3000/api/prompt',
      { prompt: this.prompt, ai: this.selectedAI },
      { withCredentials: true }
    ).subscribe({
      next: (response) => {
        this.response = response.resposta;
        this.loading = false;
      },
      error: (error) => {
      console.error('Full error:', error);

      if (error.status === 500) {
        this.response = `Erro no servidor: ${error.error?.erro || 'Tente novamente'}`;
      } else {
        this.response = 'Erro de conexão. Verifique o servidor.';
      }

      this.loading = false;
    }
    });
  }


  logout() {
    this.authService.logout();
  }
}
